import { Component } from "react";

export class Count extends Component {
    state = {
        count: 1
    }
    handleClick = () => {
        this.setState({ count: this.state.count + 1 })
    }
    render() {
        return (
            <div>
                <p> Count: {this.state.count}</p>
                <button type='button' onClick={this.handleClick}>click</button>
            </div>)
    }
}
